import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
 import { toast } from 'react-toastify';
 i
const initiaState = {
  ten_danh_muc :"",
};

export default function Editdm() {

const [state, setState] = useState(initiaState);
const {ma_danh_muc} = useParams();

useEffect(()=>{
  axios.get(`http://localhost:5000/api/getdm/${ma_danh_muc}`)
  .then((resp) => setState({...resp.data[0]}));
},[ma_danh_muc]);

const handleInputChange = (e) =>{
  const{name, value} = e.target;
  setState({...state,[name]:value});
}

const handleSubmit = (e) => {
  e.preventDefault();

  if(!ten_danh_muc){
    toast.error("Vui lòng nhập đầy đủ thông tin");

  } else{
    axios.post(`http://localhost:5000/api/updatedm/${id}`,{
      ten_danh_muc
    }).then(()=>{
      setState({ten_danh_muc :""})
    }).catch((err) => toast.error(err.response.data));
    toast.success("Sửa danh mục thành công !")
    setTimeout(() => navigator("/Indexdm"),500);
  }
 
}
  return (
    <div>
          <div>
      <h3 className="mb-0">Thêm danh mục</h3>
      <hr />
      <form onSubmit={handleSubmit} enctype="multipart/form-data">
          <div className="row mb-3">
              <div className="col">
                  <input type="text" name="ten_danh_muc" id ="ten_danh_muc" onChange={handleInputChange} value={ten_danh_muc} className="form-control" placeholder="Nhập tên danh mục..."/>
              </div>
          </div>

          <div className="row">
              <div className="d-grid">
                  <button style={{marginLeft: '10px'}} type="submit" className="btn btn-primary">Thêm</button>
              </div>
          </div>
          
      </form>
      
    </div>
    </div>
  )
}
