import React, { Fragment, useEffect, useState } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';

export default function Indexsp() {

    const [data,setData] = useState([]);

    const loadData = async() =>{
        const response = await axios.get("http://localhost:5000/api/getallsp");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);
  return (
    <div className="card shadow mb-4">
        <div className="d-flex align-items-center justify-content-between card-header py-3">
            <h6 className="m-0 font-weight-bold text-primary">Dữ Liệu Sản Phẩm</h6>
            <a href="" className="btn btn-primary">Thêm sản phẩm</a>
        </div>
        <div className="card-body">
            <div className="table-responsive">
                <table className="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Mã</th>
                            <th>Hình ảnh</th>
                            <th>Tên sản phẩm</th>
                            <th>Số lượng</th>
                            <th>Chi tiết</th>
                            <th>Sửa</th>
                            <th>Xóa</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map}
                        <tr>
                            <td>{}</td>
                            <td><img style={{borderRadius: '5px'}} src="{{ asset($item->anh_sanpham) }}" width='60' height='60' className="img img-responsive" /></td>
                            <td></td>
                            <td></td>
                            <td><a href="" type="button" className="btn btn-primary">Chi Tiết</a></td>
                            <td><a href="" className="btn btn-warning">Sửa</a></td>
                            <td>
                                    <button type='submit' onClick="" className='btn btn-danger'>Xóa</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
  );
}
