import React, { useState } from 'react';
import {Link , useHistory ,useParams} from 'react-router-dom';
import axios from 'axios';
import {toast} from 'react-toastify';

const initiaState = {
  ten_danh_muc :"",
};

export default function Createdm() {
  const [state, setState] = useState(initiaState);

  const{ten_danh_muc} = state;

  const history = u

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!ten_danh_muc){
      toast.error("Vui lòng nhập đầy đủ thông tin");
    }
    axios.post("http://localhost:5000/api/cratedm",{
      ten_danh_muc
    }).then(()=>{
      setState({ten_danh_muc :""})
    }).catch((err) => toast.error(err.response.data));

    setTimeout(()=>{
      history.push("/Indexdm");
    },500)
  }

  const handleInputChange = (e) =>{
    const{ten_danh_muc, value} = e.target;
    setState({...state,[ten_danh_muc]:value});
  }



  return (
    <div>
      <h3 className="mb-0">Thêm danh mục</h3>
      <hr />
      <form onSubmit={handleSubmit} enctype="multipart/form-data">
          <div className="row mb-3">
              <div className="col">
                  <input type="text" name="ten_danh_muc" id ="ten_danh_muc" onChange={handleInputChange} value={ten_danh_muc} className="form-control" placeholder="Nhập tên danh mục . . ."/>
              </div>
          </div>

          <div className="row">
              <div className="d-grid">
                  <button style={{marginLeft: '10px'}} type="submit" className="btn btn-primary">Thêm</button>
              </div>
          </div>
          
      </form>
      
    </div>
  )
}
