import React, { Fragment, useEffect, useState } from 'react';
import axios from "axios"; 
import{toast , Flip} from "react-toastify"
import { Link } from 'react-router-dom';

export default function Indexdm() {

        const [data , setData] = useState([]);

        const loadData = async() =>{
                const response = await axios.get("http://localhost:5000/api/getdm");
                setData(response.data);
        };

        useEffect(()=>{
            loadData();
        },[]);

        const deleteDM = (ma_danh_muc) =>{
            if(window.confirm("Bạn có muốn xóa danh mục này không ?")){
                axios.delete(`http://localhost:5000/api/removedm/${ma_danh_muc}`);
                toast.success('X', {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                    transition: Flip,
                    });
                setTimeout(()=>loadData(),500);
            }
        }
  return (

    <Fragment>
    <div className="card shadow mb-4">
            <div className="d-flex align-items-center justify-content-between card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary">Dữ Liệu Sản Phẩm</h6>
                <a href="" className="btn btn-primary">Thêm sản phẩm</a>
            </div>
            <div className="card-body">
                <div className="table-responsive">
                    <table className="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                        <thead>
                            <tr>
                                <th>Mã</th>
                                <th>Tên Danh Mục</th>
                                <th>Chi Tiết</th>
                                <th>Sửa</th>
                                <th>Xóa</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item)=>{
                                return(
                                    
                                <tr key={item.ma_danh_muc}>
                                    <td>{item.ma_danh_muc}</td>
                                    <td>{item.ten_danh_muc}</td>
                                    <td><Link to={`/view/${item.ma_danh_muc}`} type="button" class="btn btn-primary">Chi Tiết</Link></td>
                                    <td><a href="" class="btn btn-warning">Sửa</a></td>
                                    <td><button  className="btn btn-danger" onClick={() => deleteDM(item.ma_danh_muc) } >Xóa</button></td>
                                </tr>
                                )
                            })}

                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    </Fragment>
    );
}
