import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

export default function Viewhoadon() {
    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };  
    const [chiTiet ,setData] = useState({});

    const{ma_don_hang} = useParams();

    useEffect(()=>{
        axios.get(`http://localhost:5000/api/getctdh/${ma_don_hang}`)
        .then((resp) => setData({...resp.data[0]}));
    },[ma_don_hang]);
  return (
    <div>
            <div class="card shadow mb-4">
        <div class="d-flex align-items-center justify-content-between card-header py-3">
            <h6 class="m-0 font-weight-bold text-primary">Thông tin đơn hàng</h6>
            <a href="" class="btn btn-primary">Thêm chi tiết </a>
        </div>
        <div class="card-body">
            <div class="table-responsive">
                <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Mã đơn hàng</th>
                            <th>Mã sản phẩm</th>
                            <th>Tên sản phẩm</th>
                            <th>Kích cỡ</th>
                            <th>Màu sắc</th>
                            <th>Số lượng</th>
                            <th>Giá</th>

                        </tr>
                    </thead>
                    <tbody>
                        {chiTiet.map((item)=>{
                            return(
                            <tr key={item.chi_tiet_don_h}>
                                <td>{chiTiet.ma_don_hang}</td>
                                <td>{chiTiet.ma_san_pham}</td>
                                <td>{chiTiet.ten_san_pham}</td>
                                <td>{chiTiet.kich_co}</td>
                                <td>{chiTiet.mau_sac}</td>
                                <td>{chiTiet.so_luong}</td>
                                <td>{formatCurrency(chiTiet.gia)}</td>
                                
                            </tr>)
                        })}
                        

                    </tbody>

               
                </table>
            </div>
        </div>
    </div>
    </div>
  )
}
