import React, { Fragment, useEffect, useState } from 'react';
import axios from "axios";
import {toast} from "react-toastify";

export default function Main() {
const [data , setData] = useState([]);
const loadData = async() =>{
    try {
        const response = await axios.get('http://localhost:5000/api/get');
        console.log(response.data);
      } catch (error) {
        console.error('There was an error!', error);
      }
}

useEffect(()=>{
    loadData();
},[]);


  return (

<Fragment>
<div className="card shadow mb-4">
        <div className="d-flex align-items-center justify-content-between card-header py-3">
            <h6 className="m-0 font-weight-bold text-primary">Dữ Liệu Sản Phẩm</h6>
            <a href="" className="btn btn-primary">Thêm sản phẩm</a>
        </div>
        <div className="card-body">
            <div className="table-responsive">
                <table className="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Mã</th>
                            <th>Hình ảnh</th>
                            <th>Tên sản phẩm</th>
                            <th>Số lượng</th>
                            <th>Chi tiết</th>
                            <th>Sửa</th>
                            <th>Xóa</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item,ĩn)=>{
                            return(
                                
                            <tr key={item.id}>
                                <td>{item.ten_san_pham}</td>
                                <td></td>
                                <td></td>
                                <td></td>
                                <td><a href="" type="button" className="btn btn-primary">Chi Tiết</a></td>
                                <td><a href="" className="btn btn-warning">Sửa</a></td>
                            </tr>
                            )
                        })

                        }

                    </tbody>
                </table>
            </div>
        </div>
    </div>
</Fragment>
  )
}
