import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
import { toast ,Flip } from 'react-toastify';
export default function Indexkh() {
    const [data,setData] = useState([]);

    const loadData = async() =>{
        const response = await axios.get("http://localhost:5000/api/getallsp");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);

    const deleteK = (ma_san_pham) => {
        if(window.confirm("Bạn có muốn xóa sản phẩm này không ?")){
            axios.delete(`http://localhost:5000/api/deletesp/${ma_san_pham}`)
            toast.success('Xóa sản phẩm thành công !', {
             position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Flip,
                });
            setTimeout(()=>loadData(),500);
        }
    }
  return (
    <div class="card shadow mb-4">
        <div class="d-flex align-items-center justify-content-between card-header py-3">
            <h6 class="m-0 font-weight-bold text-primary">Dữ Liệu Khách Hàng</h6>
            <a href="" class="btn btn-primary">Thêm khách hàng</a>
        </div>
        <div class="card-body">
            <div class="table-responsive">
                <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Mã khách hàng</th>
                            <th>Tên khách hàng</th>
                            <th>Số điện thoại</th>
                            <th>Chi tiết</th>
                            <th>Sửa</th>
                            <th>Xóa</th>

                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td>{item.ma_khach_hang}</td>
                            <td>{item.ten_khach_hang}</td>
                            <td>{item.so_dien_thoai}</td>
                            <td><Link to="" type="button" class="btn btn-primary">Chi Tiết</Link></td>
                            <td><Link to="" class="btn btn-warning">Sửa</Link></td>
                            <td>
                                    <button type='submit' onclick={()=>deleteKH(item.ma_khach_hang)} class='btn btn-danger'>Xóa</button>

                            </td>


                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
  )
}
