import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Flip, toast } from 'react-toastify';

export default function Indexkhohang() {

    const [data ,setData] = useState(ơ);

    
    const loadData =  async() =>{
        const response = await axios.get("http://localhost:5000/api/getallkhohang");
        setData(response.data);
        };

        useEffect(()=>{
            loadData();
        },[]);


        const deleteKH = (ma_san_pham) =>{
            if(window.confirm("Bạn có muốn xóa danh mục này không ?")){
                axios.delete(`http://localhost:5000/api/removekhohang/${ma_san_pham}`);
                toast.success('Xóa đơn hàng thành công !', {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                    transition: Flip,
                    });
                setTimeout(()=>loadData(),500);
            }
        }

  return (
    <div>
      <div class="card shadow mb-4">
        <div class="d-flex align-items-center justify-content-between card-header py-3">
            <h6 class="m-0 font-weight-bold text-primary">Dữ Liệu Kho Hàng</h6>
            <a href="" class="btn btn-primary">Thêm sản phẩm</a>
        </div>
        <div class="card-body">
            <div class="table-responsive">
                <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Tên sản phẩm</th>
                            <th>Ngày sản xuất</th>
                            <th>Số lượng</th>
                            <th>Chi tiết</th>
                            <th>Sửa</th>
                            <th>Xóa</th>

                        </tr>
                    </thead>

                    <tbody>
                        {data.map((item)=>{
                            return( 
                            <tr key={item.ma_san_pham}>
                                <td>{item.ten_san_pham}</td>
                                <td>{item.ngay_san_xuat}</td>
                                <td>{item.so_luong}</td>
                                <td><a href="" type="button" class="btn btn-primary">Chi Tiết</a></td>
                                <td><a href="" class="btn btn-warning">Sửa</a></td>
                                <td>
                                        <button type='submit' onClick={() => deleteKH(item.ma_san_pham)} class='btn btn-danger'>Xóa</button>
                                </td>
                            </tr>)
                        })}
                       
                    </tbody>
                </table>
            </div>
        </div>
    </div>
    </div>
  )
}
