import React, { useEffect } from "react";

function Payment() {
  useEffect(() => {
    const activePaymentItem = document.querySelector('.payments-item.active');
    if (activePaymentItem) {
      const activePaymentInput = activePaymentItem.querySelector('input');
      if (activePaymentInput) {
        activePaymentInput.checked = true;
      }
    }

    const payments = document.querySelectorAll('.payments-item');
    payments.forEach((payment, index) => {
      payment.onclick = () => {
        const activePaymentItem = document.querySelector('.payments-item.active');
        if (activePaymentItem) {
          activePaymentItem.classList.remove('active');
        }
        payment.classList.add('active');
        const activePaymentInput = payment.querySelector('input');
        if (activePaymentInput) {
          activePaymentInput.checked = true;
        }
      };
    });

    $(".payments-item").each(function(index, value) {
      $(value).click(function() {
        $(".check").each(function() {
          if (this.checked) {
            $(".type-payment").text(this.value);
          }
        });
      });
    });
  }, []);

  return null; // Không trả về bất kỳ giao diện người dùng nào
}

export default Payment;
