import { Fragment, useEffect, useState } from "react";
import Productt from "../../until/layoutauto";
import useProductFilter from "../../until/fillter";
import { Link } from "react-router-dom";
import AddProduct from "../../until/cart";
import axios from 'axios';

export default function Product() {
    Productt();
    useProductFilter();
    AddProduct();

    const [data, setData] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [filterSize, setFilterSize] = useState("");
    const [filterType, setFilterType] = useState("");
    const [sortOrder, setSortOrder] = useState("");


    const handleSearch = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/search", {
                params: {
                    searchTerm: searchTerm,
                    size: filterSize,
                    type: filterType,
                    sortOrder: sortOrder
                }
            });
            setData(response.data);
        } catch (error) {
            console.error("Error fetching data", error);
        }
    };
    
    const loadData = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/getallsp");
            setData(response.data);
        } catch (error) {
            console.error("Error fetching data", error);
        }
    };

    useEffect(() => {
        loadData();
    }, []);
// Chạy useEffect này mỗi khi data thay đổi

const formatCurrency = (number) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
};  

    return (
        <Fragment>
            <div className="all-product-container" style={{ paddingBottom: '30px' }}>
                <div className="filter">
                    <h2>Sản phẩm</h2>
                    <div className="filter-search">
                        <input 
                            placeholder="Tìm kiếm sản phẩm..." 
                            type="text" 
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button onClick={handleSearch}>
                            <img src="../Images/icon-search.svg" alt="" />
                        </button>
                    </div>
                    <div className="filter-size filter-item">
                        <div className="filter-item__inner">
                            <span>Kích cỡ</span>
                            <i className="fa-solid fa-angle-down"></i>
                        </div>
                        <ul className="filter-item__option">
                            <li onClick={() => setFilterSize("XS")}>XS</li>
                            <li onClick={() => setFilterSize("S")}>S</li>
                            <li onClick={() => setFilterSize("M")}>M</li>
                            <li onClick={() => setFilterSize("L")}>L</li>
                            <li onClick={() => setFilterSize("XL")}>XL</li>
                            <li onClick={() => setFilterSize("2XL")}>2XL</li>
                            <li onClick={() => setFilterSize("3XL")}>3XL</li>
                        </ul>
                    </div>
                    <div className="filter-type filter-item">
                        <div className="filter-item__inner">
                            <span>Nhóm sản phẩm</span>
                            <i className="fa-solid fa-angle-down"></i>
                        </div>
                        <ul className="filter-item__option">
                            <li onClick={() => setFilterType("Áo polo")}>Áo polo</li>
                            <li onClick={() => setFilterType("Áo tee")}>Áo tee</li>
                            <li onClick={() => setFilterType("Áo thể thao")}>Áo thể thao</li>
                        </ul>
                    </div>
                    <div className="filter-sort filter-item">
                        <div className="filter-item__inner">
                            <span>Sắp xếp</span>
                            <i className="fa-solid fa-angle-down"></i>
                        </div>
                        <ul className="filter-item__option">
                            <li onClick={() => setSortOrder("newest")}>Mới nhất</li>
                            <li onClick={() => setSortOrder("best_selling")}>Bán chạy</li>
                            <li onClick={() => setSortOrder("low_to_high")}>Giá thấp đến cao</li>
                            <li onClick={() => setSortOrder("high_to_low")}>Giá cao đến thấp</li>
                            <li onClick={() => setSortOrder("most_discount")}>% Giảm nhiều nhất</li>
                        </ul>
                    </div>

                </div>
                <div className="container1">
                    <div className="product-type">
                        <h2>Áo T-shirt</h2>
                        <div className="row">
                            {/* Sản phẩm mẫu */}
                            <div className="col p-2-4">
                                <div className="product">
                                    <img src="https://media.coolmate.me/cdn-cgi/image/quality=80/uploads/April2022/ao_thun.jpg" alt="" style={{ borderRadius: '16px', width: '100%', height: '100%' }} />
                                </div>
                            </div>

                            {/* Render sản phẩm từ dữ liệu */}
                            {data.map((item) => (
                                <div key={item.ma_san_pham} className="col p-2-4">
                                    <div id={`product-${item.ma_san_pham}`} className="product">
                                        <div className="product-img-wrap" style={{ marginBottom: '8px' }}>
                                            <Link to={`/detail/${item.ma_san_pham}`} className="product-img product-img--small">
                                                <img className="product-img-1" src={item.anh_sanpham} alt="" />
                                                <img className="product-img-2" src={item.anhhover1} alt="" />
                                            </Link>
                                            <div className="product-size">
                                                <p>Thêm nhanh vào giỏ hàng +</p>
                                                <div className="btn btn--size">S</div>
                                                <div className="btn btn--size">M</div>
                                                <div className="btn btn--size">L</div>
                                                <div className="btn btn--size">XL</div>
                                                <div className="btn btn--size">2XL</div>
                                            </div>
                                        </div>
                                        <div className="product-content">
                                            <div style={{ display: 'none' }} className="product-content__option ">
                                                <div className="product-content__option-item-wrap active">
                                                    <span data={item.mau_sac}></span>
                                                </div>
                                            </div>
                                            <a className="product-name">{item.ten_san_pham}</a>
                                            <div className="product-price-wrap">
                                                <div className="product-price">{formatCurrency(item.gia)}</div>
                                            </div>
                                            <div className="product-discount">
                                                Mua 2 chỉ còn 189k/chiếc
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
