import React, { Fragment, useEffect, useState } from 'react';
import axios from "axios";
export default function Odercart() {
    const [data,setData] = useState([]);

    const loadData = async() =>{
        const response = await axios.get(`http://localhost:5000/api/orderDetailsByCustomer/`);
        setData(response.data);
    };

    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };

    useEffect(()=>{
        loadData();
    },[]);
  return (
    <Fragment>
        <div className='container-cart'>
        <h3>Thông tin đơn hàng</h3>
       <div className="product-table">
        
            <table>
                <thead>
                    <tr>
                        <th>Tên Sản Phẩm</th>
                        <th>Ảnh Sản Phẩm</th>
                        <th>Số Lượng</th>
                        <th>Kích Cỡ</th>
                        <th>Màu Sắc</th>
                        <th>Thành Tiền</th>
                        <th>Trạng thái đơn hàng</th>
                        <th>Thao tác</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item)=>{
                        return(<tr>
                            <td className='table-cart-1'>{item.ten_san_pham}</td>
                            <td><img src={item.anh_sanpham} className="product-image" /></td>
                            <td>{item.so_luong}</td>
                            <td>{item.kich_co}</td>
                            <td>{item.mau_sac}</td>
                            <td>{formatCurrency(item.gia)}</td>
                            <td>Đang đợi xác nhận</td>
                            <td><i class="fas fa-pen"></i></td>
                        </tr>)
                        
                    })}
                    

                </tbody>
            </table>
        </div>
        </div>
      
    </Fragment>
  )
}
