import React, { useEffect } from "react";

function Payment() {
  useEffect(() => {

    const $ = window.$;
    const payments = document.querySelectorAll('.payments-item');
    const paymentsInput = document.querySelector('.payments-item input');
    document.querySelector('.payments-item.active .check').checked = true;

    payments.forEach((payment, index) => {
      payment.onclick = () => {
        document.querySelector('.payments-item.active').classList.remove('active');
        payment.classList.add('active');
        document.querySelector('.payments-item.active .check').checked = true; // Khi check vào item thì sẽ check vào input
      };
    });

    $(".payments-item").each(function(index, value) {
      $(value).click(function() {
        // Duyệt qua nếu check được chọn thì gán value
        $(".check").each(function() {
          if (this.checked) {
            $(".type-payment").text(this.value);
          }
        });
      });
    });
  }, []);

  return null; // Không trả về bất kỳ giao diện người dùng nào
}

export default Payment;
