import React, { Fragment, useEffect, useState } from 'react';
import axios from "axios";
import { useUser } from '../../until/userContext';

export default function Odercart() {
    const { user } = useUser();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true); // State để kiểm tra trạng thái loading
    const [error, setError] = useState(''); // State để lưu lỗi khi có

    const loadData = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/orderDetailsByCustomer/${user.id}`);
            setData(response.data);
            setLoading(false); // Tắt trạng thái loading khi có dữ liệu
        } catch (error) {
            setError('Hiện chưa có đơn hàng nào !'); // Xử lý lỗi khi gọi API
            setLoading(false); // Tắt trạng thái loading khi có lỗi
        }
    };

    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };

    useEffect(() => {
        loadData();
    }, []);

    return (
        <Fragment>
            <div className='container-cart'>
                <h2>Thông tin đơn hàng</h2>
                {loading ? (<p>Đang tải dữ liệu...</p>
) : error ? (
                    <p style={{ color: 'red' }}>{error}</p>
                ) : (

                    <div className="product-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>Tên Sản Phẩm</th>
                                    <th>Ảnh Sản Phẩm</th>
                                    <th>Số Lượng</th>
                                    <th>Kích Cỡ</th>
                                    <th>Màu Sắc</th>
                                    <th>Thành Tiền</th>
                                    <th>Trạng thái đơn hàng</th>
                                    <th>Thao tác</th>
                                </tr>
                            </thead>
                            <tbody>
                                {data.map((item) => (
                                    <tr key={item.ma_chi_tiet_don_hang}>
                                        <td className='table-cart-1'>{item.ten_san_pham}</td>
                                        <td><img src={item.anh_sanpham} className="product-image" alt="Product" /></td>
                                        <td>{item.so_luong}</td>
                                        <td>{item.kich_co}</td>
                                        <td>{item.mau_sac}</td>
                                        <td>{formatCurrency(item.gia)}</td>
                                        <td>Đang đợi xác nhận</td>
                                        <td><i className="fas fa-pen"></i></td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </Fragment>
    );
}
