import React, { useState } from 'react';

const Filter = () => {
  const [filterState, setFilterState] = useState({
    oldIndex: null,
    activeFilters: [],
  });

  const handleFilterClick = (index) => {
    setFilterState((prevState) => {
      let newActiveFilters = [...prevState.activeFilters];
      let checkFilter = prevState.oldIndex === index ? !newActiveFilters[index] : true;

      newActiveFilters = newActiveFilters.map((_, i) => (i === index ? checkFilter : false));

      return {
        oldIndex: index,
        activeFilters: newActiveFilters,
      };
    });
  };

  const handleFilterOptionClick = (className, text) => {
    document.querySelector(`.${className} .filter-item__inner span`).textContent = text;
  };

  return (
    <div className="filters">
      {['filter-size', 'filter-type', 'filter-sort'].map((filterClass, index) => (
        <ul key={filterClass} className={filterClass}>
          <li className="filter-item" onClick={() => handleFilterClick(index)}>
            Filter Item
          </li>
          {/* Add other filter options here */}
          <li onClick={() => handleFilterOptionClick(filterClass, 'Option 1')}>Option 1</li>
          <li onClick={() => handleFilterOptionClick(filterClass, 'Option 2')}>Option 2</li>
          <li onClick={() => handleFilterOptionClick(filterClass, 'Option 3')}>Option 3</li>
        </ul>
      ))}
    </div>
  );
};

export default Filter;
