import React, { useEffect } from "react";

function Payment() {
  useEffect(() => {

    const $ = window.$;

    $(document).ready(function(){
        var heightHeader = document.querySelector('.site-header').offsetHeight;
        document.querySelector('main').style.margin = `${heightHeader}px 0 0`;
    
        var imgs=$(".product-img__option-item img")
        var imgOption=$(".product-img__option-item img")
    
        $(".btn-size").each(function(index,value){
            $(value).click(function(){
                $(".content__size-header span:first-child").html(`Kích thước: <b>${$(this).text()}</b>`)
                $(".btn-addCart").html("Thêm vào giỏ hàng")
                $(".btn-size.active").removeClass("active")
                $(this).addClass("active")
            })
        })

    
    })
    
  }, []);

  return null; // Không trả về bất kỳ giao diện người dùng nào
}

export default Payment;
