import React, { useEffect, useState } from "react";

function ActiveCart() {
  const [list, setList] = useState(JSON.parse(localStorage.getItem('cart')) || []);
  const [total, setTotal] = useState(0);
  const [tamtinh, setTamtinh] = useState(0);
  const [deliveryCost, setDeliveryCost] = useState('Miễn phí');

  useEffect(() => {
    loadData();
  }, [list]);

  const loadData = () => {
    let newTotal = 0;
    let newTamtinh = 0;
    for (let x of list) {
      newTotal += (x.price * x.quantity) - x.discount;
      newTamtinh += (x.price * x.quantity);
    }
    if (newTotal < 200) {
      newTotal += 25000;
      setDeliveryCost('25.000đ');
    } else {
      setDeliveryCost('Miễn phí');
    }
    setTotal(newTotal);
    setTamtinh(newTamtinh);
  };

  const convertVND = (number) => {
    if (number === 0) return '0đ';
    const str = number.toString();
    return str.replace(/\B(?=(\d{3})+(?!\d))/g, ".") + 'đ';
  };

  const handleDecrease = (id) => {
    setList(list.map(item =>
      item.id === id ? { ...item, quantity: Math.max(item.quantity - 1, 1) } : item
    ));
  };

  const handleIncrease = (id) => {
    setList(list.map(item =>
      item.id === id ? { ...item, quantity: item.quantity + 1 } : item
    ));
  };

  const handleRemove = (id, size, color) => {
    setList(list.filter(item => !(item.id === id && item.size === size && item.color === color)));
  };

  return (
    <div className="list-product__inner">
      {list.map(item => (
        <div key={item.id} className="list-product__item">
          <div className="list-product__item-img">
            <img src={item.img} alt="" />
          </div>
          <div className="list-product__item-content">
            <div className="list-product__item-name">{item.name}</div>
            <div className="list-product__item-type">{item.color}/{item.size}</div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div className="quantity-product">
                <button onClick={() => handleDecrease(item.id)}>
                  <svg width="16" height="16" xmlns="http://www.w3.org/2000/svg">
                    <g>
                      <line strokeWidth="1.5" y2="8" x2="10" y1="8" x1="5" stroke="#000000" fill="none" />
                    </g>
                  </svg>
                </button>
                <span>{item.quantity}</span>
                <button onClick={() => handleIncrease(item.id)}>
                  <svg width="16" height="16" xmlns="http://www.w3.org/2000/svg">
                    <g>
                      <line strokeWidth="1.5" y2="8" x2="12.9695" y1="8" x1="3.0305" stroke="#000000" fill="none" />
                      <line strokeWidth="1.5" transform="rotate(90, 8, 8)" y2="8" x2="13" y1="8" x1="3" stroke="#000000" fill="none" />
                    </g>
                  </svg>
                </button>
              </div>
              <div className="product-price">
                <div className="product-new-price">{convertVND(item.price)}</div>
              </div>
            </div>
            <div className="list-product__close" onClick={() => handleRemove(item.id, item.size, item.color)}>
              <i className="fa-solid fa-xmark"></i>
            </div>
          </div>
        </div>
      ))}
      <div className="delever-cost">{deliveryCost}</div>
      <div className="total__price">{convertVND(total)}</div>
      <div className="tamTinh">{convertVND(tamtinh)}</div>
      <button className="btn-pay" style={{ opacity: list.length === 0 ? 0.5 : 1, pointerEvents: list.length === 0 ? 'none' : 'auto' }}>
        <span className="btn-pay--price">{convertVND(total)}</span>
      </button>
    </div>
  );
}

export default ActiveCart;
