import React from 'react'
import { Routes,Route, Router } from 'react-router-dom';
import Navbar from './components/Navbar/navbar'
import Footer from './components/Footer/footer'
import Home from './pages/index/Home';
import Cartpage from './pages/cart/Cartpage';
import Product from './pages/product/Product';
import Details from './pages/product/Details';
import DangKy from './pages/login/DangKy';
import DangNhap from './pages/login/DangNhap';
import Chonsize from './pages/tuvan/Chonsize';
import About from './pages/about/About';
import Message from './components/Message/message';
import Buycoolmate from './components/Message/buycoolmate';
import ScrollToTop from './until/scroll';

export default function App() {
  return (
  
     
    <div className='app'>
      
      <Navbar/>
        <Routes>
        <Route path='/' element={<Home/>}/>
        <Route path='/cart' element={<Cartpage/>}/>
        <Route path='/product' element={<Product/>}/>
        <Route path='/detail' element={<Details/>}/>
        <Route path='/dangky' element={<DangKy/>}/>
        <Route path='/about' element={<About/>}/>
        <Route path='/dangnhap' element={<DangNhap/>}/>
        <Route path='/chonsize' element={<Chonsize/>}/>
        <Route path='/detail/:ma_san_pham' element={<Details/>}/>
        </Routes>
      <Message/>
      <Buycoolmate/>
      <Footer/>
    </div>
 
  )
}
