import { useEffect } from 'react';

export function StartSlider() {
    var list = document.getElementsByClassName("slide-img");
    var index = 0;
    for (var x of list) { 
      x.style.display = "none";
    }
  
    list[index].style.display = "block";
  
    function showL() {
      for (var x of list) { 
        x.style.display = "none";
      }
      if (index == 0) index = list.length - 1;
      else index = index - 1;
      list[index].style.display = "block";
      setTimeout(showL, 2000);
    }
  
    function showR() {
      for (var x of list) { 
        x.style.display = "none";
      }
      if (index == list.length - 1) index = 0;
      else index = index + 1;
      list[index].style.display = "block";
      setTimeout(showR, 2000);
    }
  
    setTimeout(showL, 2000);

    // Trả về các hàm để sử dụng trong useEffect
    return { showL, showR };
}

// Sử dụng trong component
function MyComponent() {
    useEffect(() => {
        const { showL, showR } = StartSlider();

        // Xóa bỏ hoặc dừng hàm khi component unmount hoặc chuyển hướng
        return () => {
            clearTimeout(showL);
            clearTimeout(showR);
        };
    }, []);
}
