import React, { useState, useEffect, useRef } from 'react';

const ProductContainer = () => {
  const [headerHeight, setHeaderHeight] = useState(0);
  const headerRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    // Function to set header height
    const updateHeaderHeight = () => {
      if (headerRef.current) {
        const newHeight = headerRef.current.offsetHeight;
        console.log('Header height:', newHeight); // Debugging line
        setHeaderHeight(newHeight);
      }
    };

    // Initial setting of header height
    updateHeaderHeight();

    // Update header height on window resize
    window.addEventListener('resize', updateHeaderHeight);

    // Cleanup event listener on unmount
    return () => {
      window.removeEventListener('resize', updateHeaderHeight);
    };
  }, []);

  useEffect(() => {
    // Apply margin-top to the container
    if (containerRef.current) {
      console.log('Setting margin-top:', `${headerHeight}px`); // Debugging line
      containerRef.current.style.marginTop = `${headerHeight}px`;
    }
  }, [headerHeight]);

  return (
    <>
      <header className="site-header" ref={headerRef}>
        {/* Header content */}
      </header>
      <div className="all-product-container" ref={containerRef}>
        {/* Product content */}
      </div>
    </>
  );
};

export default ProductContainer;
