export function StartSlider() {
  var list = document.getElementsByClassName("slide-img");
  var index = 0;

  // Kiểm tra xem có phần tử nào trong list không
  if (list.length > 0) {
      // Ẩn tất cả các phần tử có class "slide-img"
      for (var x of list) { 
          x.style.display = "none";
      }

      // Hiển thị phần tử đầu tiên
      list[index].style.display = "block";

      function showL() {
          // Ẩn tất cả các phần tử có class "slide-img"
          for (var x of list) { 
              x.style.display = "none";
          }
          if (index == 0) index = list.length - 1;
          else index = index - 1;
          list[index].style.display = "block";
          setTimeout(showL, 2000);
      }

      function showR() {
          // Ẩn tất cả các phần tử có class "slide-img"
          for (var x of list) { 
              x.style.display = "none";
          }
          if (index == list.length - 1) index = 0;
          else index = index + 1;
          list[index].style.display = "block";
          setTimeout(showR, 2000);
      }

      setTimeout(showL, 2000);
  } else {
      console.error("No element with class 'slide-img' found.");
  }
}
