import React, { Fragment, useEffect, useState } from 'react';
import axios from 'axios';
import { useUser } from '../../until/userContext';
import './Odercart.css'; // Import CSS để quản lý kiểu dáng

const Loading = () => (
    <div className="loading">
        <p>Đang tải dữ liệu...</p>
    </div>
);

const Error = ({ message }) => (
    <div className="error">
        <p style={{ color: 'red' }}>{message}</p>
    </div>
);

export default function Odercart() {
    const { user } = useUser();
    const [customerInfo, setCustomerInfo] = useState({});
    const [orderDetails, setOrderDetails] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const loadData = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/orderDetailsByCustomer/${user.id}`);
            const data = response.data;
            
            // Cập nhật state với dữ liệu mới
            setCustomerInfo(data.customerInfo);
            setOrderDetails(data.orderDetails);
            
            setLoading(false);
        } catch (error) {
            setError('Hiện chưa có đơn hàng nào!');
            setLoading(false);
        }
    };

    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };

    useEffect(() => {
        loadData();
    }, []);

    return (
        <Fragment>
            <div className="container-cart">
                <h2>Thông tin đơn hàng</h2>
                
                <div className="address-details">
                    <h3><i className="fas fa-map-marker-alt"></i> Địa chỉ nhận hàng</h3>
                    <div className="address-item">
                        <label>Tên người nhận:</label>
                        <span>{customerInfo.ten_khach}</span>
                    </div>
                    <div className="address-item">
                        <label>Số điện thoại:</label>
                        <span>{customerInfo.sdt}</span>
                    </div>
                    <div className="address-item">
                        <label>Địa chỉ chi tiết:</label>
                        <span>{customerInfo.dia_chi}</span>
                    </div>
                </div>
                
                {loading ? (
                    <Loading />
                ) : error ? (
                    <Error message={error} />
                ) : (
                    <div className="product-table">
                        {orderDetails.length > 0 ? (
                            <table>
                                <thead>
                                    <tr>
                                        <th>Tên Sản Phẩm</th>
                                        <th>Ảnh Sản Phẩm</th>
                                        <th>Số Lượng</th>
                                        <th>Kích Cỡ</th>
                                        <th>Màu Sắc</th>
                                        <th>Thành Tiền</th>
                                        <th>Trạng thái đơn hàng</th>
                                        <th>Thao tác</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {orderDetails.map((item) => (
                                        <tr key={item.ma_chi_tiet_don_hang}>
                                            <td className="table-cart-1">{item.ten_san_pham}</td>
                                            <td>
                                                <img 
                                                    src={item.anh_sanpham} 
                                                    className="product-image" 
                                                    alt="Product" 
                                                    loading="lazy" // Lazy load image
                                                />
                                            </td>
                                            <td>{item.so_luong}</td>
                                            <td>{item.kich_co}</td>
                                            <td>{item.mau_sac}</td>
                                            <td>{formatCurrency(item.gia)}</td>
                                            <td>Đang đợi xác nhận</td>
                                            <td><i className="fas fa-pen"></i></td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <div className="no-orders">
                                <p>Không có đơn hàng nào được tìm thấy.</p>
                            </div>
                        )}
                    </div>
                )}
            </div>
        </Fragment>
    );
}
