import React, { useState, useEffect, useCallback } from 'react';

const CombinedComponent = ({ product }) => {
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = useCallback((item) => {
    setCart((prevCart) => {
      let list = [...prevCart];
      let check = true;

      for (let x of list) {
        if (x.id === item.id && x.color === item.color && x.size === item.size) {
          x.quantity += 1;
          check = false;
          break;
        }
      }

      if (check) {
        list.push({ ...item, quantity: 1 });
      }

      return list;
    });

    loadMiniCart();
    displayNotify(item);
  }, []);

  const loadMiniCart = () => {
    // Implement hàm để tải mini cart
  };

  const displayNotify = (item) => {
    // Implement hàm để hiển thị thông báo
  };

  useEffect(() => {
    const buttons = document.querySelectorAll(`.btn--size-${product.id}`);
    buttons.forEach(button => {
      button.addEventListener('click', handleSizeClick);
    });

    return () => {
      buttons.forEach(button => {
        button.removeEventListener('click', handleSizeClick);
      });
    };
  }, [product, addToCart]);

  const handleSizeClick = (event) => {
    const size = event.target.textContent;
    const item = {
      id: product.id,
      name: product.name,
      img: product.img,
      color: product.color,
      size: size,
      price: product.price,
      quantity: 1,
      discount: 0,
    };
    addToCart(item);
  };

};

export default CombinedComponent;
