        );
        $(".header__actions-cart-notify").text("0");
        $(".added-product").text("0");
      }
    }
    loadMiniCart();

    // Cập nhật giỏ hàng
    function updateCart() {
      localStorage.setItem("cart", JSON.stringify(list));
    }

    // xóa sản phẩm dựa trên id
    function Xoa(id, size, color) {
