import React, { useState, useEffect } from 'react';
 
 export const AddToCart = ({ item }) => {
  const [cart, setCart] = useState(() => {
    // Khởi tạo cart từ localStorage
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    // Cập nhật localStorage mỗi khi cart thay đổi
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (item) => {
    setCart((prevCart) => {
      let list = [...prevCart];
      let check = true;

      // Tìm trong list xem đã tồn tại product người dùng vừa thêm chưa
      for (let x of list) {
        if (x.id === item.id && x.color === item.color && x.size === item.size) {
          x.quantity += 1;
          check = false;
          break;
        }
      }

      // Nếu chưa thì thêm product mới
      if (check) {
        list.push({ ...item, quantity: 1 });
      }

      return list;
    });

    loadMiniCart();
    displayNotify(item);
  };

  const loadMiniCart = () => {
    // Implement hàm để tải mini cart
  };

  const displayNotify = (item) => {
    // Implement hàm để hiển thị thông báo
  };

};

export const CLicksize = ({ product, addToCart }) => {
    useEffect(() => {
      const buttons = document.querySelectorAll(`.btn--size-${product.id}`);
      buttons.forEach(button => {
        button.addEventListener('click', handleSizeClick);
      });
  
      return () => {
        buttons.forEach(button => {
          button.removeEventListener('click', handleSizeClick);
        });
      };
    }, [product]);
  
    const handleSizeClick = (event) => {
      const size = event.target.textContent;
      const item = {
        id: product.id,
        name: product.name,
        img: product.img,
        color: product.color,
        size: size,
        price: product.price,
        quantity: 1,
        discount: 0,
      };
      addToCart(item);
    };
  };
