import React, { useEffect, useState } from 'react';

function AddProduct() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    function addToCart(item) {
      setCart(prevCart => {
        let updatedCart = [...prevCart];
        let found = false;

        // Check if the item already exists in cart
        updatedCart.forEach(x => {
          if (x.id === item.id && x.color === item.color && x.size === item.size) {
            x.quantity += item.quantity;
            found = true;
          }
        });

        // If item not found, add it to cart
        if (!found) {
          updatedCart.push(item);
        }

        // Update localStorage
        localStorage.setItem("cart", JSON.stringify(updatedCart));

        // Update mini cart and display notification
        loadMiniCart();
        displayNotify(item);

        return updatedCart;
      });
    }

    // Handle click on button with class 'btn-addCart'
    $('.btn-addCart').on('click', function() {
      if($(this).text() === "Thêm vào giỏ hàng") {
        const name = $(".content__heading").text();
        const img = $(".product-img__option-item.active img").attr("src");
        const color = $(".content__color-heading b").text();
        const size = $(".btn-size.active").text();
        const price = convertToNumber($(".content__price").text());
        const quantity = JSON.parse($(".product-single__actions .quantity span").text());

        const item = {
          id: "detail1",
          name: name,
          img: img,
          color: color,
          size: size,
          price: price,
          quantity: quantity,
          discount: 0
        };

        addToCart(item); // Thêm thông tin vào giỏ hàng
      }
    });

    // Function to remove item from mini cart
    function removeItemFromCart(id, size, color) {
      setCart(prevCart => {
        let updatedCart = prevCart.filter(x => !(x.id === id && x.size === size && x.color === color));
        localStorage.setItem("cart", JSON.stringify(updatedCart));
        loadMiniCart();
        return updatedCart;
      });
    }

    window.XoaMiniCart = removeItemFromCart;

    // Function to load mini cart
    function loadMiniCart() {
      const updatedCart = JSON.parse(localStorage.getItem("cart")) || [];
      const length = updatedCart.length;
      let str = "";

      if (length > 0) {
        updatedCart.forEach(x => {
          str += `<li class="mini-cart__item">
                    <a class="mini-cart__link">
                      <div class="mini-cart__link-img">
                        <img src="${x.img}" alt="">
                      </div>
                      <div class="mini-cart__link-content">
                        <p class="mini-cart__link-content-name">${x.name}</p>
                        <p class="mini-cart__link-content-describe">${x.color}${x.size}</p>
                        <p class="mini-cart__link-content-price">${convertVND(x.price)}</p>
                        <p class="mini-cart__link-content-quantity">x${x.quantity}</p>
                        <span class="mini-cart__item-cancel" onClick="XoaMiniCart('${x.id}','${x.size}','${x.color}')">✕</span>
                      </div>
                    </a>
                  </li>`;
        });
      } else {
        str = '<p class="cart-empty">Không có sản phẩm</p>';
      }

      document.querySelector(".mini-cart__list").innerHTML = str;
      document.querySelector(".header__actions-cart-notify").textContent = `${length}`;
      document.querySelector(".added-product").textContent = `${length}`;
    }

    // Function to display notification
    function displayNotify(item) {
      $(".notify-added-img img").attr("src", `${item.img}`);
      $(".notify-product__title").text(`${item.name}`);
      $(".notify-product__color").text(`${item.color}`);
      $(".notify-product__size").text(`${item.size}`);
      $(".notify-product__prices").text(`${convertVND(item.price)}`);
      $(".notify-added").css("transform", "translateX(0px)");

      setTimeout(function () {
        $(".notify-added").css("transform", "translateX(calc(100% + 20px))");
      }, 3000);
    }

    // Utility function to convert price to number
    function convertToNumber(price) {
      let result = "";
      for (let i = 0; i < price.length; i++) {
        if (price[i] !== "." && price[i] !== "đ") {
          result += price[i];
        }
      }
      return parseInt(result, 10);
    }

    // Utility function to convert number to VND format
    function convertVND(number) {
      if (number == 0) {
        return "0đ";
      }
      const str = JSON.stringify(number);
      let result = "";
      const length = str.length;
      let count = 0;
      for (let i = length - 1; i >= 0; --i) {
        if (count % 3 === 0 && count !== 0) {
          result = str[i] + "." + result;
        } else {
          result = str[i] + result;
        }
        count++;
      }
      return result + "đ";
    }

    // Clean-up function for removing event listeners or other clean-up tasks
    return () => {
      $('.btn-addCart').off('click'); // Remove event listener on unmount if needed
    };

  }, []); // Empty dependency array ensures useEffect runs once on mount

  return null; // or return appropriate JSX if needed
}

export default AddProduct;
