import React, { useEffect } from 'react';

function AddProduct() {
  useEffect(() => {

    
    const $ = window.$;

    function addToCart(item) {
      var list;
      if (localStorage.getItem('cart') == null) {
        list = [item];
      } else {
        list = JSON.parse(localStorage.getItem('cart')) || [];
        let check = true;
        for (let x of list) {
          if (x.id == item.id && x.color == item.color && x.size == item.size) {
            x.quantity += 1;
            check = false;
            break;
          }
        }
        if (check) {
          list.push(item);
        }
      }
      localStorage.setItem('cart', JSON.stringify(list));
      displayNotify(item); // Hàm hiển thị thông báo đã thêm vào giỏ
    }



    function displayNotify(item) {
        $(".notify-added-img img").attr("src", `${item.img}`); // load link ảnh
        $(".notify-product__title").text(`${item.name}`);
        $(".notify-product__color").text(`${item.color}`);
        $(".notify-product__size").text(`${item.size}`);
        $(".notify-product__prices").text(`${convertToNumber(item.price)}`)
        $(".notify-added").css("transform", "translateX(0px)"); // đưa block thông báo dịch chuyển vào trong màn hình
        
        // block thông báo được thu vào sau 4 giây
        setTimeout(function () {
          $(".notify-added").css("transform", "translateX(calc(100% + 20px))");
        }, 30000000);
    }


    $('.btn--size').each(function(indexBtn, valueBtn) {
      $(valueBtn).click(function() {
        var id = $(valueBtn).closest('.product').attr("id");
        var name = $(`#${id} .product-name`).text();
        var img = $(`#${id} .product-img-1`).attr("src");
        var color = $(`#${id} .product-content__option-item-wrap.active span`).attr("data");
        var size = $(valueBtn).text();
        var price = co($(`#${id} .product-price`).text());
        var item = {
          id: id,
          name: name,
          img: img,
          color: color,
          size: size,
          price: price,
          quantity: 1,
          discount: 0
        };
        addToCart(item);
      });
    });

    function convertToNumber(price) {
        let result = "";
        for (let i = 0; i < price.length; i++) {
          if (price[i] !== "." && price[i] !== "đ") {
            result += price[i];
          }
        }
        return parseInt(result, 10);
      }

      //Hàm chuyển từ số sang chuỗi theo định dạng VND
     function convertVND(number) {
      if(number==0) {return '0đ'}
      var str=JSON.stringify(number);
      var result=""
      var length=str.length
      var count=0
      for(var i=length-1; i>=0; --i) {
          if(count%3==0 && count!=0) {
              result=str[i]+'.'+result
          }
          else {
              result=str[i]+result
          }
          count++
      }
      return result+'đ'
  }
      


  }, []);

}

export default AddProduct;
