import React, { useEffect } from 'react';

function AddProduct() {
  useEffect(() => {
    const $ = window.$;

    function addToCart(item) {
      var list;
      if (localStorage.getItem('cart') == null) {
        list = [item];
      } else {
        list = JSON.parse(localStorage.getItem('cart')) || [];
        let check = true;
        for (let x of list) {
          if (x.id == item.id && x.color == item.color && x.size == item.size) {
            x.quantity += 1;
            check = false;
            break;
          }
        }
        if (check) {
          list.push(item);
        }
      }
      localStorage.setItem('cart', JSON.stringify(list));
      loadMiniCart(); // Hàm tải lại giỏ hàng mini
      displayNotify(item); // Hàm hiển thị thông báo đã thêm vào giỏ
    }

    function loadMiniCart() {
      // Implement hàm để tải lại thông tin giỏ hàng mini
      console.log("Mini cart loaded");
    }

    function displayNotify(item) {
      // Implement hàm để hiển thị thông báo khi thêm sản phẩm vào giỏ hàng
      alert(`Đã thêm ${item.name} vào giỏ hàng`);
    }

    $('.btn--size').each(function(indexBtn, valueBtn) {
      $(valueBtn).click(function() {
        var id = $(valueBtn).closest('.product').attr("id");
        var name = $(`#${id} .product-name`).text();
        var img = $(`#${id} .product-img-1`).attr("src");
        var color = $(`#${id} .product-content__option-item-wrap.active span`).attr("data");
        var size = $(valueBtn).text();
        var price = convertToNumber($(`#${id} .product-price`).text());
        var item = {
          id: id,
          name: name,
          img: img,
          color: color,
          size: size,
          price: price,
          quantity: 1,
          discount: 0
        };
        addToCart(item);
      });
    });

    function convertToNumber(price) {
      return parseInt(price.replace(/\D/g, '')); // Hàm chuyển đổi giá tiền từ chuỗi sang số
    }
  }, []);


}

export default AddProduct;
